];viewTarget = AppMan.Utils.idJoin(hash, 'settings-form');AppMan.addInterval(viewTarget, intid);}</script><?php $query = "SELECT Users.Options, Users.id FROM #wp__easycontactforms_customformfields AS CustomFormFields  LEFT JOIN #wp__easycontactforms_customforms AS CustomForms  LEFT JOIN #wp__easycontactforms_users AS Users  ON CustomForms.ObjectOwner = Users.id  ON CustomFormFields.CustomForms = CustomForms.id WHERE CustomFormFields.id = '$id'";$objs = EasyContactFormsDB::getObjects($query);$uoptsxml = '<data></data>';if (count($objs) == 1 && !empty($objs[0]->Options)) {	$uoptsxml = $objs[0]->Options;}$viewName = 'first';$uoptsxml = simplexml_load_string($uoptsxml);if (!isset($uoptsxml->vcita) && count($objs) == 1) {	$uoptsxml->addChild('vcita');	$uoptsxml->vcita->FirstName = 'Demo';	$uoptsxml->vcita->LastName = '';	$uoptsxml->vcita->Demo = 'true';	$viewName = 'third';}$uopts = $uoptsxml->vcita;$userid = null;if (count($objs) != 1 || is_null($objs[0]->id)) {	$uopts->Error = 'Primary notification receiver is not set';}else {	$userid = $objs[0]->id;	$user = EasyContactFormsClassLoader::getObject('Users', true, $objs[0]->id);}if (isset($uopts->vcid) && !empty($uopts->vcid) && $viewName != 'third') {	$result = file_get_contents("http://www.vcita.com/api/experts/{$uopts->vcid}");	if ($result) {		$result = json_decode($result);		if (is_object($result)) {			$uxml = $user->get('Options');			$uxml = simplexml_load_string($uxml);			if ($result->success && $result->confirmed) {				$viewName = 'third';				$uxml->vcita->Confirmed = 'true';				$user->set('Options', $uxml->asXML());				$user->save();				$fld = EasyContactFormsClassLoader::getObject('CustomFormFields', true, $id);				$fld->updateTemplate();			}			else {				$viewName = 'second';			}			$uopts->Error = isset($result->error) && !empty($result->error) ? $result->error : '';		}		else {			$uopts->Error = 'Cannot connect to vcita';		}	}	else {		$uopts->Error = 'Cannot connect to vcita';	}}switch ($viewName) {	case 'first':