<field><?php if (isset($ShowDescription) && $ShowDescription == 'on') { ?><ShowDescription position="<?php if (isset($DescriptionPosition) && !empty($DescriptionPosition)) { echo $DescriptionPosition; } else {echo 'top';} ?>"><![CDATA[<div <?php if (isset($DescriptionCSSClass) && !empty($DescriptionCSSClass)) {echo "class='$DescriptionCSSClass'";}; if (isset($DescriptionCSSStyle) && !empty($DescriptionCSSStyle)) {echo " style='$DescriptionCSSStyle'";}; ?>><?php if (isset($Description)) {echo $Description; }; ?></div>]]></ShowDescription><?php } ?><Container containertag="fieldset" addcf="<?php echo (isset($AddCF) && $AddCF == 'on') ? 'on' : 'off';?>"><![CDATA[<fieldset<?php if (isset($CSSClass)  && !empty($CSSClass)) { echo " class='$CSSClass'"; };$style = array();if (isset($SetSize) && $SetSize == 'on' && isset($WidthUnit) && isset($Width)) {$style[] = "width:{$Width}{$WidthUnit}";};if (isset($SetStyle) && $SetStyle == 'on' && isset($CSSStyle) && !empty($CSSStyle)) { $style[] = $CSSStyle; };if (count($style) > 0) {echo " style='" . implode(';', $style) . "'";};?>><?php if (isset($ShowLabel) && $ShowLabel == 'on') { ?><legend <?php if (isset($LabelCSSClass) && !empty($LabelCSSClass)) {echo "class='$LabelCSSClass'";};if (isset($LabelCSSStyle) && !empty($LabelCSSStyle)) {echo "style='$LabelCSSStyle'";};?>><?php echo $Label; ?></legend><?php } ?>]]></Container></field>