<field><?php if (isset($ShowLabel) && $ShowLabel == 'on') { ?><ShowLabel position="<?php if (isset($LabelPosition) && !empty($LabelPosition)) { $position = explode('-', $LabelPosition); $position = $position[0]; echo $position; } else {echo 'left';} ?>"><![CDATA[<label for='ufo-field-id-<?php echo $id;?>' <?php if (isset($LabelCSSClass) && !empty($LabelCSSClass)) {echo "class='$LabelCSSClass'";}; if (isset($LabelPosition)) { $style = array(); $align = explode('-',$LabelPosition); $align = $align[count($align)-1]; $style[] = "text-align:$align"; }; if (isset($LabelCSSStyle) && !empty($LabelCSSStyle)) { $style = array(); $style[] = $LabelCSSStyle; }; if (count($style) > 0) {echo " style='". implode(';',$style). "'";}; ?>><?php echo $Label;?></label>]]></ShowLabel><?php } ?><Input <?php if (isset($SetSize) && $SetSize == 'on' && isset($Width) && !empty($Width) && isset($WidthUnit) && $WidthUnit != 'chars') {echo ' width="' . $Width . $WidthUnit . '"';}; ?>><![CDATA[<div <?php if (isset($DescriptionCSSClass) && !empty($DescriptionCSSClass)) {echo "class='$DescriptionCSSClass'";}; if (isset($DescriptionCSSStyle) && !empty($DescriptionCSSStyle)) {echo " style='$DescriptionCSSStyle'";}; if (isset($SetSize) && $SetSize == 'on' && isset($Width) && !empty($Width) && isset($WidthUnit) && $WidthUnit == 'chars') {echo "size='$Width'";}; ?>><?php if (isset($ShowDescription) && $ShowDescription == 'on') { ?><?php if (isset($Description)) {echo $Description; }; ?><?php } ?></div>]]></Input></field>